mapper.delete:
DELETE FROM t_capture_rank_job
WHERE fQZSettingUuid = #{qzSettingUuid}

    AND fOperationType = #{operationType}

mapper.select:
SELECT 1 FROM t_capture_rank_job c WHERE ( c.fExectionStatus = 'New' OR (c.fExectionStatus = 'Complete' AND c.fExectionType = 'Everyday' AND c.fLastExecutionDate < curdate()) )
AND fRankJobArea = #{rankJobArea}
AND c.fCaptureRankJobStatus = 1 AND c.fQZSettingUuid IS NULL and c.fGroupNames IN
#{groupName}
LIMIT 1

mapper.update:
UPDATE t_capture_rank_job
SET fUpdateTime = NOW(), fUpdateBy = #{updateBy}, fCaptureRankJobStatus = #{captureRankJobStatus}
WHERE fUuid IN

    #{uuid}

mapper.select:
SELECT

FROM t_capture_rank_job c
WHERE 1 = 1

    AND 1 != 1


    AND c.fGroupNames LIKE CONCAT(#{criteria.groupNames}, '%')


    AND c.fCustomerUuid = #{criteria.customerUuid}


    AND c.fOperationType = #{criteria.operationType}


    AND c.fExectionType = #{criteria.exectionType}


    AND c.fExectionStatus = #{criteria.exectionStatus}



        AND c.fRankJobType = #{criteria.rankJobType}
        AND c.fQZSettingUuid IS NULL


        AND c.fQZSettingUuid > ''


        AND c.fRankJobType = #{criteria.rankJobType}



    AND c.fRankJobArea = #{criteria.rankJobArea}


    AND c.fRankJobCity = #{criteria.rankJobCity}

SELECT COUNT(*) FROM t_customer_keyword WHERE fTerminalType = #{captureRankJob.operationType} AND fOptimizeGroupName = #{captureRankJob.groupNames}
AND fCustomerUuid = #{captureRankJob.customerUuid}
AND fStatus = 1 AND fCaptureStatus = 1 AND fCapturePositionQueryTime < DATE_SUB(NOW(),INTERVAL 2 MINUTE)

select
from t_capture_rank_job c where c.fExectionStatus='Processing' AND c.fCaptureRankJobStatus = 1
AND c.fRankJobType = #{captureJobCriteria.rankJobType}
AND c.fRankJobArea = #{captureJobCriteria.rankJobArea}
AND c.fRankJobCity = #{captureJobCriteria.rankJobCity}
AND c.fRankJobCity IS NULL
order By c.fExectionTime limit 1

SELECT
FROM t_capture_rank_job c WHERE ( c.fExectionStatus='New' OR ( c.fExectionStatus='Complete' AND c.fExectionType='Everyday' AND (c.fLastExecutionDate < curdate() OR c.fLastExecutionDate IS NULL) AND DATEDIFF(NOW(),fLastExecutionDate) >= fCaptureDaysInterval ) ) AND c.fExectionTime <= curtime() AND c.fCaptureRankJobStatus = 1
AND c.fRankJobType = #{captureJobCriteria.rankJobType}
AND fQZSettingUuid IS NULL
AND fQZSettingUuid IS NOT NULL
AND c.fRankJobArea = #{captureJobCriteria.rankJobArea}
AND c.fRankJobCity = #{captureJobCriteria.rankJobCity}
AND c.fRankJobCity IS NULL
ORDER BY c.fExectionTime limit 1
