mapper.delete:
DELETE FROM t_capture_rank_job
WHERE fQZSettingUuid = #{qzSettingUuid}

  AND fOperationType = #{operationType}

mapper.select:
SELECT 1 FROM t_capture_rank_job c WHERE ( c.fExectionStatus = 'New' OR (c.fExectionStatus = 'Complete' AND c.fExectionType = 'Everyday' AND c.fLastExecutionDate < curdate()) )
AND fRankJobArea = #{rankJobArea}
AND c.fCaptureRankJobStatus = 1 and c.fGroupNames IN
#{groupName}
LIMIT 1
