mapper.select:
SELECT
COUNT(1)
FROM
t_customer_keyword ck,
t_customer c
WHERE c.fUuid = ck.fCustomerUuid
AND ck.fStatus = #{keywordStandardCriteria.status}
AND ck.fType = #{keywordStandardCriteria.type}
AND ck.fTerminalType = #{keywordStandardCriteria.terminalType}
AND ck.fLastReachStandardDate <= DATE_SUB(CURRENT_DATE(), INTERVAL #{keywordStandardCriteria.noReachStandardDays} DAY)

SELECT ck.fOptimizeGroupName AS 'groupName' FROM t_customer_keyword ck WHERE ck.fTerminalType = #{groupSettingCriteria.terminalType}
AND ck.fOptimizeGroupName LIKE CONCAT(#{groupSettingCriteria.optimizedGroupName},'%')
AND ck.fOptimizeGroupName > '' AND ck.fStatus = 1 GROUP BY ck.fOptimizeGroupName

SELECT ck.fKeyword AS 'keyword', ck.fSearchEngine AS 'searchEngine', COUNT(1) AS 'keywordCount', SUM(IF(ck.fCurrentPosition BETWEEN 1 AND 3, 1, 0)) AS 'topThree', SUM(IF(ck.fCurrentPosition BETWEEN 1 AND 10, 1, 0)) AS 'topTen', COUNT(DISTINCT ck.fCustomerUuid) AS 'customerCount', COUNT(DISTINCT c.fUserID) AS 'userCount' FROM t_customer_keyword ck, t_customer c WHERE c.fUuid = ck.fCustomerUuid AND ck.fType IN ('pt', 'qt') AND ck.fTerminalType = #{keywordCriteria.terminalType}
AND ck.fKeyword like concat(#{keywordCriteria.keyword}, '%')
AND ck.fSearchEngine = #{keywordCriteria.searchEngine}
AND c.fUserID = #{keywordCriteria.userName}
GROUP BY ck.fKeyword, ck.fSearchEngine ORDER BY keywordCount DESC

