mapper.select:
SELECT
SUM(IF(cps.fPosition BETWEEN 1 AND 3, 1, 0)) AS 'topThreeCount',
SUM(IF(cps.fPosition BETWEEN 1 AND 5, 1, 0)) AS 'topFiveCount',
SUM(IF(cps.fPosition BETWEEN 1 AND 10, 1, 0)) AS 'topTenCount',
SUM(IF(cps.fPosition BETWEEN 1 AND 50, 1, 0)) AS 'topFifthCount',
cps.fCreateDate AS 'date'
FROM t_ck_position_summary cps
LEFT JOIN t_customer_keyword ck
ON cps.fCustomerKeywordUuid = ck.fUuid
WHERE 1 = 1

    AND cps.fCreateDate >= #{condition.ltDate}


    AND cps.fCreateDate < #{condition.gtDate}


    AND ck.fCustomerUuid = #{condition.customer}


    AND ck.fSearchEngine = #{condition.searchEngine}


    AND ck.fTerminalType = #{condition.terminal}


    AND ck.fType = #{condition.type}

GROUP BY cps.fCreateDate

SELECT ck.fKeyword AS 'keyword', c.fContactPerson AS 'customer', cps.fCustomerKeywordUuid AS 'customerKeywordUuid', cps.fPosition AS 'position', ck.fSearchEngine AS 'searchEngine', ck.fTerminalType AS 'terminal', ck.fType AS 'type', cps.fCreateDate AS 'date' FROM t_ck_position_summary cps LEFT JOIN t_customer_keyword ck ON cps.fCustomerKeywordUuid = ck.fUuid LEFT JOIN t_customer c ON ck.fCustomerUuid = c.fUuid WHERE 1 = 1
AND cps.fCreateDate >= #{condition.dateStart}
AND cps.fCreateDate < #{condition.dateEnd}
AND ck.fSearchEngine = #{condition.searchEngine}
AND ck.fTerminalType = #{condition.terminal}
AND ck.fCustomerUuid = #{condition.customer}
AND ck.fType = #{condition.type}
AND ck.fKeyword LIKE CONCAT(#{condition.keyword},'%')
GROUP BY cps.fCustomerKeywordUuid LIMIT #{condition.start}, #{condition.limit};

SELECT COUNT(1) FROM (
SELECT 1
FROM t_ck_position_summary cps
LEFT JOIN t_customer_keyword ck
ON cps.fCustomerKeywordUuid = ck.fUuid
WHERE 1 = 1

    AND cps.fCreateDate >= #{condition.dateStart}


    AND cps.fCreateDate < #{condition.dateEnd}


    AND ck.fSearchEngine = #{condition.searchEngine}


    AND ck.fTerminalType = #{condition.terminal}


    AND ck.fCustomerUuid = #{condition.customer}


    AND ck.fType = #{condition.type}


    AND ck.fKeyword LIKE CONCAT(#{condition.keyword},'%')

GROUP BY cps.fCustomerKeywordUuid ) T

SELECT
    GROUP_CONCAT(fPosition)   AS 'hData',
    GROUP_CONCAT(fCreateDate) AS 'hDate'
FROM t_ck_position_summary
WHERE fCustomerKeywordUuid = #{condition.uuid}
AND fCreateDate >= #{condition.fifteenDayAgo}
AND fCreateDate < #{condition.today}
GROUP BY fCustomerKeywordUuid
