mapper.select:
SELECT ck.fOptimizeGroupName AS "group", ck.fType AS "type", ck.fTerminalType AS "terminalType", 4 AS "maxInvalidCount", COUNT(1) AS "totalKeywordCount", SUM(IF(ck.fOptimizeRemainingCount > 0, 1, 0)) AS "needOptimizeKeywordCount", SUM(IF(ck.fInvalidRefreshCount >= 4, 1, 0)) AS "invalidKeywordCount", SUM(IF(ck.fFailedCause > '', 1, 0)) AS "failedKeywordCount", SUM(IF(ck.fOptimizedCount = 0, 1, 0)) AS "zeroOptimizedCount", SUM(IF(ck.fLastReachStandardDate = CURRENT_DATE(), 1, 0)) AS "reachStandardKeywordCount", SUM(IF(ck.fLastReachStandardDate = CURRENT_DATE(),ck.fTodayFee, 0)) as "todaySubTotal", SUM(ck.fOptimizePlanCount) AS "totalOptimizeCount", SUM(ck.fOptimizedCount) AS "totalOptimizedCount", SUM(ck.fOptimizeRemainingCount) AS "needOptimizeCount", SUM(ck.fQueryCount) AS "queryCount" FROM t_customer_keyword ck, t_group g
, t_customer c
, t_qz_operation_type ot, t_qz_category_tag qc
AND ck.fCustomerUuid = c.fUuid
AND ck.fOptimizeGroupName = g.fGroupName
AND ot.fGroup = g.fGroupName AND ot.fQZSettingUuid = qc.fQZSettingUuid
AND ck.fType = #{criteria.entryType}
AND ck.fTerminalType = #{criteria.terminalType}
AND ck.fOptimizeGroupName like concat(#{criteria.groupName}, '%') AND g.fGroupName like concat(#{criteria.groupName}, '%')
AND ck.fMachineGroup = #{criteria.machineGroup}
AND c.fContactPerson like concat('%', #{criteria.customerName}, '%')
AND c.fUserID = #{criteria.userName}
AND qc.fTagName = #{criteria.categoryTag}
AND ck.fStatus = 1 GROUP BY ck.fOptimizeGroupName, ck.fType, ck.fTerminalType ORDER BY ck.fOptimizeGroupName
