mapper.update:
UPDATE t_group_setting
SET fUpdateTime = NOW()
 ,fOperationType = #{groupSetting.operationType}
 ,fMachineUsedPercent = #{groupSetting.machineUsedPercent}
 ,fDisableStatistics = #{groupSetting.disableStatistics}
 ,fDisableVisitWebsite = #{groupSetting.disableVisitWebsite}
 ,fPage = #{groupSetting.page}
 ,fPageSize = #{groupSetting.pageSize}
 ,fClearCookie = #{groupSetting.clearCookie}
 ,fEntryPageMinCount = #{groupSetting.entryPageMinCount}
 ,fEntryPageMaxCount = #{groupSetting.entryPageMaxCount}
 ,fPageRemainMinTime = #{groupSetting.pageRemainMinTime}
 ,fPageRemainMaxTime = #{groupSetting.pageRemainMaxTime}
 ,fInputDelayMinTime = #{groupSetting.inputDelayMinTime}
 ,fInputDelayMaxTime = #{groupSetting.inputDelayMaxTime}
 ,fSlideDelayMinTime = #{groupSetting.slideDelayMinTime}
 ,fSlideDelayMaxTime = #{groupSetting.slideDelayMaxTime}
 ,fTitleRemainMinTime = #{groupSetting.titleRemainMinTime}
 ,fTitleRemainMaxTime = #{groupSetting.titleRemainMaxTime}
 ,fOptimizeKeywordCountPerIP = #{groupSetting.optimizeKeywordCountPerIP}
 ,fRandomlyClickNoResult = #{groupSetting.randomlyClickNoResult}
WHERE fUuid = #{groupSetting.uuid}

mapper.select:
SELECT gs.fUuid
FROM t_group_setting gs
WHERE gs.fOperationCombineUuid = #{operationCombineUuid}
