mapper.select:
SELECT

FROM t_industry_info tii,
u_userinfo u
WHERE u.fLoginName = tii.fUserID
AND tii.fUserID = #{industryCriteria.loginName}
AND tii.fSearchEngine = #{industryCriteria.searchEngine}
AND tii.fIndustryName like CONCAT('%', #{industryCriteria.industryName}, '%')
AND tii.fTerminalType = #{industryCriteria.terminalType}
AND tii.fStatus = #{industryCriteria.status}
AND u.fOrganizationID = #{industryCriteria.organizationID}
ORDER BY tii.fCreateTime DESC

mapper.delete:
DELETE FROM t_industry_info
WHERE fUuid IN

    #{uuid}
