mapper.select:
SELECT ck.fMachineGroup AS "machineGroup", COUNT(1) AS "totalKeywordCount", SUM(IF(ck.fOptimizeRemainingCount > 0, 1, 0)) AS "needOptimizeKeywordCount", SUM(IF(ck.fInvalidRefreshCount >= toc.fMaxInvalidCount, 1, 0)) AS "invalidKeywordCount", SUM(IF(ck.fOptimizedCount = 0, 1, 0)) AS "zeroOptimizedCount", SUM(IF(ck.fLastReachStandardDate = CURRENT_DATE(), 1, 0)) AS "reachStandardKeywordCount", SUM(IF(ck.fLastReachStandardDate = CURRENT_DATE(), ck.fTodayFee, 0)) AS "todaySubTotal", SUM(ck.fOptimizePlanCount) AS "totalOptimizeCount", SUM(ck.fOptimizedCount) AS "totalOptimizedCount", SUM(ck.fOptimizeRemainingCount) AS "needOptimizeCount", SUM(ck.fQueryCount) AS "queryCount" FROM t_customer_keyword ck, t_group g, t_operation_combine toc WHERE ck.fStatus = 1 AND toc.fUuid = g.fOperationCombineUuid AND ck.fOptimizeGroupName = g.fGroupName AND ck.fTerminalType = toc.fTerminalType
AND ck.fMachineGroup like concat(#{criteria.machineGroup}, '%')
AND ck.fType = #{criteria.entryType}
AND ck.fTerminalType = #{criteria.terminalType}
AND EXISTS (SELECT 1 FROM t_customer c WHERE ck.fCustomerUuid = c.fUuid AND c.fContactPerson LIKE CONCAT('%', #{criteria.customerName}, '%'))
GROUP BY ck.fMachineGroup, ck.fType, ck.fTerminalType ORDER BY ck.fMachineGroup;

SELECT mgwi.fMachineGroup AS 'machineGroup', mgwi.fTotalKeywordCount AS 'totalKeywordCount', mgwi.fNeedOptimizeKeywordCount AS 'needOptimizeKeywordCount', mgwi.fInvalidKeywordCount AS 'invalidKeywordCount', mgwi.fZeroOptimizedCount AS 'zeroOptimizedCount', mgwi.fReachStandardKeywordCount AS 'reachStandardKeywordCount', mgwi.fTodaySubTotal AS 'todaySubTotal', mgwi.fTotalOptimizeCount AS 'totalOptimizeCount', mgwi.fTotalOptimizedCount AS 'totalOptimizedCount', mgwi.fNeedOptimizeCount AS 'needOptimizeCount', mgwi.fQueryCount AS 'queryCount', mgwi.fTotalMachineCount AS 'totalMachineCount', mgwi.fUnworkMachineCount AS 'unworkMachineCount', mgwi.fMaxInvalidCount AS 'maxInvalidCount', mgwi.fIdleTotalMinutes AS 'idleTotalMinutes' FROM t_machine_group_work_info mgwi WHERE date_format(#{criteria.dayNum},'%Y-%m-%d') = mgwi.fCreateDate AND mgwi.fType = #{criteria.entryType} AND mgwi.fTerminalType = #{criteria.terminalType}
AND mgwi.fMachineGroup LIKE CONCAT(#{criteria.machineGroup}, "%")
GROUP BY mgwi.fUuid ORDER BY mgwi.fMachineGroup
