mapper.update:
UPDATE t_machine_info SET fTargetVersion = #{targetVersion}
WHERE fClientID IN

    #{item}

UPDATE t_machine_info SET fTargetVPSPassword = #{targetVPSPassword}
WHERE fClientID IN

    #{item}

mapper.select:
SELECT
FROM t_machine_info mi WHERE mi.fTerminalType = #{machineInfoCriteria.terminalType} AND mi.fClientID like '${machineInfoCriteria.clientID}%'
AND mi.fMachineGroup like '${machineInfoCriteria.machineGroup}%'
AND mi.fUpgradeFailedReason like '${machineInfoCriteria.upgradeFailedReason}%'
AND mi.fVersion like concat(#{machineInfoCriteria.version},'%')
AND mi.fTargetVersion like concat(#{machineInfoCriteria.targetVersion},'%')
AND mi.fCity like concat(#{machineInfoCriteria.city},'%')
AND mi.fValid = #{machineInfoCriteria.valid}
AND (mi.fContinuousFailCount > 5 OR mi.fTenMinsLastVisitTime < NOW()) and mi.fValid = 1
AND mi.fRenewalDate < DATE_ADD(CURRENT_DATE(), INTERVAL -3 DAY) and mi.fValid = 1
AND (mi.fMachineGroup is null or mi.fMachineGroup = '')
AND mi.fMachineGroup > ''
AND mi.fStartUpStatus = #{machineInfoCriteria.startUpStatus}
AND mi.fRunningProgramType = #{machineInfoCriteria.runningProgramType}
AND (mi.fHost is null or mi.fHost = '') AND (mi.fVersion <> mi.fTargetVersion)
AND (mi.fPassword <> mi.fTargetVPSPassword)
AND mi.fDownloadProgramType > ''
AND mi.fVPSBackendSystemComputerID = #{machineInfoCriteria.vpsBackendSystemComputerID}
AND mi.fSwitchGroupName = #{machineInfoCriteria.switchGroupName}
AND mi.fAllowSwitchGroup = #{machineInfoCriteria.allowSwitchGroup}
AND mi.fMemory >= #{machineInfoCriteria.gtMemory} AND mi.fMemory <= #{machineInfoCriteria.ltMemory} AND mi.fSwitchGroupName IN
#{switchGroup}
ORDER BY ${machineInfoCriteria.orderBy}

SELECT

FROM
t_machine_info mi
WHERE mi.fTerminalType = #{machineInfoCriteria.terminalType}
AND mi.fMachineGroup = #{machineInfoCriteria.machineGroup}
AND (mi.fContinuousFailCount > 5 OR mi.fTenMinsLastVisitTime < NOW()) and mi.fValid = 1
AND
    mi.fGroup = #{machineInfoCriteria.groupName}

AND mi.fSwitchGroupName IN

        #{switchGroup}

SELECT

FROM
t_machine_info mi
WHERE mi.fTerminalType = #{terminalType}
AND mi.fClientID = #{clientID}

SELECT COUNT(1) FROM t_machine_info
WHERE fVersion = #{clientUpgrade.version}
AND fRunningProgramType = #{clientUpgrade.programType}
AND fTargetVersion = #{clientUpgrade.targetVersion}
AND fTerminalType = #{clientUpgrade.terminalType}

SELECT COUNT(1) FROM t_machine_info
WHERE fVersion = #{clientUpgrade.version}
AND fRunningProgramType = #{clientUpgrade.programType}
AND (fTargetVersion is null or fTargetVersion <> #{clientUpgrade.targetVersion})
AND fTerminalType = #{clientUpgrade.terminalType}

mapper.update:
UPDATE t_machine_info SET fStartUpStatus = 'Completed'
WHERE fClientID IN

    #{clientID}

UPDATE t_machine_info
SET fTerminalType = fTerminalType
,fMachineGroup = #{machineInfo.machineGroup}
,fAllowSwitchGroup = #{machineInfo.allowSwitchGroup}
,fSwitchGroupName = #{machineInfo.switchGroupName}
,fHost = #{machineInfo.host}
,fPort = #{machineInfo.port}
,fUserName = #{machineInfo.userName}
,fBroadbandAccount = #{machineInfo.broadbandAccount}
,fBroadbandPassword = #{machineInfo.broadbandPassword}
,fVpsBackendSystemComputerID =
    #{machineInfo.vpsBackendSystemComputerID}

,fVpsBackendSystemPassword =
    #{machineInfo.vpsBackendSystemPassword}

WHERE fClientID IN

    #{clientID}

UPDATE t_machine_info mi
SET mi.fAllowSwitchGroup = #{machineInfoCriteria.allowSwitchGroup}

    ,mi.fSwitchGroupName = #{machineInfoCriteria.switchGroupName}


    ,mi.fMachineGroup = #{machineInfoCriteria.targetMachineGroup}

WHERE 1=1
AND mi.fClientID IN

        #{item}

UPDATE t_machine_info
SET fUpgradeFailedReason = #{upgradeFailedReason}
WHERE fClientID IN

    #{clientID}

