    #{clientID}

UPDATE t_machine_info
SET fValid = #{valid}
WHERE fClientID IN

    #{clientID}

UPDATE t_machine_info
SET fTerminalType = #{terminalType}
WHERE fClientID IN

    #{clientID}

UPDATE t_machine_info
SET fTargetVersion = #{clientUpgrade.targetVersion}
WHERE fVersion = #{clientUpgrade.version}
AND fRunningProgramType = #{clientUpgrade.programType}
AND fTerminalType = #{clientUpgrade.terminalType}
AND (fTargetVersion is null or fTargetVersion <> #{clientUpgrade.targetVersion})
LIMIT #{clientUpgrade.maxUpgradeCount}

UPDATE t_machine_info
SET
fLastVisitTime = NOW(),
fTenMinsLastVisitTime = DATE_ADD(NOW(), INTERVAL 10 MINUTE ),
fPageNo = #{pageNo}
WHERE fClientID = #{clientID}

UPDATE t_machine_info
SET fRestartStatus = #{restartStatus}, fRestartTime = now(), fThreeMinsRestartTime = DATE_ADD(NOW(), INTERVAL 3 MINUTE ), fTenMinsRestartTime = DATE_ADD(NOW(), INTERVAL 10 MINUTE ), fRestartOrderingTime = now()
WHERE fClientID = #{clientID}

UPDATE t_machine_info mi SET mi.fMachineGroup = #{machineInfoCriteria.targetMachineGroup} WHERE 1=1 AND mi.fClientID IN
#{item}
AND mi.fClientID like '${machineInfoCriteria.clientID}%'
AND mi.fMachineGroup like '%${machineInfoCriteria.machineGroup}%'
AND mi.fUpgradeFailedReason like '%${machineInfoCriteria.upgradeFailedReason}%'
AND mi.fVersion like concat(#{machineInfoCriteria.version},'%')
AND mi.fTargetVersion like concat(#{machineInfoCriteria.targetVersion},'%')
AND mi.fCity like concat(#{machineInfoCriteria.city},'%')
AND mi.fValid = #{machineInfoCriteria.valid}
AND (mi.fContinuousFailCount > 5 OR mi.fTenMinsLastVisitTime < NOW()) and mi.fValid = 1
AND mi.fRenewalDate < DATE_ADD(CURRENT_DATE(), INTERVAL -3 DAY) and mi.fValid = 1
AND (mi.fMachineGroup is null or mi.fMachineGroup = '')
AND mi.fMachineGroup > ''
AND mi.fStartUpStatus = #{machineInfoCriteria.startUpStatus}
AND mi.fRunningProgramType = #{machineInfoCriteria.runningProgramType}
AND (mi.fHost is null or mi.fHost = '') AND (mi.fVersion <> mi.fTargetVersion)
AND (mi.fPassword <> mi.fTargetVPSPassword)
AND mi.fDownloadProgramType > ''
AND (mi.fVPSBackendSystemComputerID = #{machineInfoCriteria.vpsBackendSystemComputerID})
AND mi.fSwitchGroupName = #{machineInfoCriteria.switchGroupName}
AND mi.fAllowSwitchGroup = #{machineInfoCriteria.allowSwitchGroup}
AND mi.fSwitchGroupName IN
#{switchGroup}
ORDER BY ${machineInfoCriteria.orderBy}
