mapper.select:
SELECT qs.fUuid AS 'uuid', c.fUserID AS 'userID', qs.fCustomerUuid AS 'customerUuid', c.fContactPerson AS 'contactPerson', o.fOrganizationName AS 'organizationName', qs.fDomain AS 'domain', qs.fSearchEngine AS 'searchEngine', qs.fBearPawNumber AS 'bearPawNumber',
qs.fPcGroup AS 'pcGroup',
qs.fPhoneGroup AS 'phoneGroup',
qs.fType AS 'type', qs.fCreateTime AS 'createTime', qs.fStatus AS 'status', qs.fRenewalStatus AS 'renewalStatus', qs.fCrawlerStatus AS 'crawlerStatus', qs.fAutoCrawlKeywordFlag AS 'autoCrawlKeywordFlag' FROM t_qz_setting qs JOIN t_customer c ON qs.fCustomerUuid = c.fUuid LEFT JOIN t_userinfo u ON u.fLoginName = c.fUserID LEFT JOIN t_organization o ON o.fUuid = u.fOrganizationID WHERE 1 = 1 AND qs.fDomain = #{qzSettingCriteria.domain} AND u.fUuid = #{qzSettingCriteria.userInfoID} AND u.fOrganizationID = #{qzSettingCriteria.organizationID} AND qs.fCustomerUuid = #{qzSettingCriteria.customerInfo} AND qs.fSearchEngine = #{qzSettingCriteria.searchEngine}
AND qs.fPcGroup > '' AND qs.fPcGroup = #{qzSettingCriteria.group}
AND qs.fPhoneGroup > '' AND qs.fPhoneGroup = #{qzSettingCriteria.group}
ORDER BY qs.fUpdateTime

SELECT COUNT(1)
FROM t_customer_keyword ck
WHERE ck.fOptimizeGroupName = #{optimizeGroupName}
AND ck.fType = 'qz'
AND ck.fTerminalType = #{terminalType}
AND ck.fStatus = 1

mapper.update:
UPDATE t_qz_setting
SET fPcGroup = #{qzSetting.pcGroup},fPhoneGroup = #{qzSetting.phoneGroup}
WHERE fUuid = #{qzSetting.uuid}

UPDATE t_qz_setting
SET fUpdateTime = NOW(), fRenewalStatus = #{renewalStatus}
WHERE fUuid IN (${uuids})
