mapper.select:
SELECT

FROM t_customer c
WHERE c.fEntryType = #{customerCriteria.entryType}
AND c.fContactPerson like '%${customerCriteria.contactPerson}%'
AND c.fType = #{customerCriteria.type}
AND c.fQQ like '${customerCriteria.qq}%'
AND c.fTelphone like '%${customerCriteria.telphone}%'
AND c.fUserID = #{customerCriteria.loginName}
AND c.fStatus = #{customerCriteria.status}
AND c.fRemark like '%${customerCriteria.remark}%'
order by c.fUpdateTime DESC

SELECT c.fUuid AS uuid, c.fContactPerson AS contactPerson FROM t_customer c RIGHT JOIN t_customer_keyword ck ON c.fUuid = ck.fCustomerUuid WHERE c.fUuid IS NOT NULL AND ck.fTerminalType = #{terminalType} AND c.fStatus = 1 AND ck.fOptimizeGroupName IN
#{groupName}
GROUP BY ck.fCustomerUuid

SELECT fType as 'type', count(1) as 'customerCount'
FROM t_customer
WHERE fEntryType = #{customerCriteria.entryType}
 AND fUserID = #{customerCriteria.loginName}
GROUP BY fType

mapper.update:
UPDATE
    t_customer
SET
    fActiveHour = #{activeHour},fInActiveHour = #{inActiveHour}
WHERE fUuid IN

    #{uuid}
