mapper.select:
SELECT fUuid AS 'uuid',fKeyword AS 'keyword'
FROM t_exclude_keyword
WHERE fQZSettingUuid = #{qzSettingUuid}
AND fTerminalType = #{terminalType}

SELECT COUNT(1)
FROM t_exclude_keyword
WHERE fTerminalType = #{terminalType}

  AND fCustomerUuid = #{customerUuid}


  AND fQZSettingUuid = #{qzSettingUuid}

AND fKeyword like '%${keyword}%'
