mapper.select:
SELECT ck.fCustomerUuid, COUNT(1) as subCount FROM t_customer_keyword ck WHERE ck.fTerminalType = #{terminalType}
and ck.fType = #{entryType} and ck.fCustomerUuid IN

    #{item}

 GROUP BY ck.fCustomerUuid

SELECT

FROM
t_customer_keyword ck,
t_customer c
WHERE c.fUuid = ck.fCustomerUuid
AND ck.fCustomerUuid = #{customerKeywordCriteria.customerUuid}
AND ck.fKeyword like '${customerKeywordCriteria.keyword}%'
AND ck.fUrl like '%${customerKeywordCriteria.url}%'
AND ck.fStatus = #{customerKeywordCriteria.status}
AND ck.fInvalidRefreshCount >= #{customerKeywordCriteria.invalidRefreshCount}
AND ck.fCreateTime >= #{customerKeywordCriteria.creationFromTime}
AND ck.fCreateTime <= #{customerKeywordCriteria.creationToTime}
AND ck.fType = #{customerKeywordCriteria.entryType}
AND fCurrentPosition > 0 and fCurrentPosition <= #{customerKeywordCriteria.position}     
AND ck.fCurrentPosition =  0    
AND ck.fOrderNumber  like '%${customerKeywordCriteria.orderNumber}%'
AND ((ck.fCollectMethod = 'PerDay' AND ck.fEffectiveToTime is not null) OR (ck.fEffectiveToTime <= DATE_ADD(CURDATE(),INTERVAL 3 DAY))) 
AND fTerminalType =  #{customerKeywordCriteria.terminalType} 
AND ck.fOptimizedCount <= #{customerKeywordCriteria.optimizedCount} 

    AND ck.fCustomerUuid in (
    SELECT fUuid FROM t_customer WHERE fQQ LIKE '${customerKeywordCriteria.qq}%' )


    AND ck.fCustomerUuid IN ( SELECT fUuid FROM t_customer c WHERE c.fUserID = (
    SELECT fLoginName FROM t_userinfo WHERE fLoginName = #{customerKeywordCriteria.userName}))



        AND ck.fOptimizeGroupName  like '%${customerKeywordCriteria.optimizeGroupName}%'



            AND (ck.fOptimizeGroupName is null or ck.fOptimizeGroupName != 'stop')





      ORDER BY ck.${customerKeywordCriteria.orderElement}  DESC


      ORDER BY ck.fKeyword

mapper.update:
UPDATE t_customer_keyword SET fOptimizeGroupName = #{customerKeywordUpdateGroupCriteria.targetGroupName},fUpdateTime = CURRENT_TIMESTAMP WHERE
fTerminalType = #{customerKeywordUpdateGroupCriteria.terminalType}  AND fType = #{customerKeywordUpdateGroupCriteria.entryType}

    AND fCustomerUuid = #{customerKeywordUpdateGroupCriteria.customerUuid}

  AND fUuid IN

         #{item}

mapper.select:
SELECT ck.fUuid as 'uuid', (ck.fOptimizePlanCount - ck.fOptimizedCount) AS 'remainingCount' FROM t_customer_keyword ck
WHERE ck.fOptimizeGroupName = #{groupName} AND ck.fOptimizePlanCount > ck.fOptimizedCount and ck.fInvalidRefreshCount < #{maxInvalidCount}
ORDER BY (ck.fOptimizePlanCount - ck.fOptimizedCount) DESC

mapper.update:
UPDATE t_customer_keyword
SET fOptimizePlanCount = #{optimizationPlanCount}, fQueryInterval = #{queryInterval}
WHERE fUuid = #{customerKeywordUuid}

mapper.select:
SELECT

FROM t_customer_keyword ck
WHERE ck.fTerminalType = #{terminalType} and ck.fUrl is not null and ck.fStatus = 1
AND (ck.fCapturePositionQueryTime is null or

    ck.fCapturePositionQueryTime < #{startTime}


    ck.fCapturePositionQueryTime < DATE_SUB(NOW(), INTERVAL #{minutes} MINUTE )

)

    AND ck.fCustomerUuid = #{customerUuid}




        AND ck.fOptimizeGroupName IN

            #{item}



        AND (ck.fOptimizeGroupName IS NULL OR ck.fOptimizeGroupName <> 'stop')


ORDER BY ck.fCapturePositionQueryTime, fUpdateTime DESC
LIMIT 1

mapper.update:
UPDATE t_customer_keyword SET fInvalidRefreshCount = 0 WHERE fType = #{customerKeywordRefreshStatInfoCriteria.entryType} and fTerminalType = #{customerKeywordRefreshStatInfoCriteria.terminalType}
AND EXISTS (SELECT 1 FROM t_customer c WHERE c.fUuid = fCustomerUuid AND c.fContactPerson LIKE concat('%', #{customerKeywordRefreshStatInfoCriteria.customerName}, '%'))
AND fOptimizeGroupName LIKE concat('%', #{customerKeywordRefreshStatInfoCriteria.groupName}, '%')

UPDATE t_customer_keyword SET fStatus = #{status}
WHERE fUuid IN

    #{uuid}
