mapper.select:
SELECT ck.fCustomerUuid, COUNT(1) as subCount FROM t_customer_keyword ck WHERE ck.fTerminalType = #{terminalType}
and ck.fType = #{entryType} and ck.fCustomerUuid IN

    #{item}

 GROUP BY ck.fCustomerUuid

SELECT

FROM  t_customer_keyword ck
WHERE 1 = 1
AND ck.fCustomerUuid = #{customerKeywordCrilteria.customerUuid}
AND ck.fKeyword like '${customerKeywordCrilteria.keyword}%'
AND ck.fUrl like '%${customerKeywordCrilteria.url}%'
AND ck.fStatus = #{customerKeywordCrilteria.status}
AND ck.fInvalidRefreshCount = #{customerKeywordCrilteria.invalidRefreshCount}
AND ck.fOptimizeGroupName  like '%${customerKeywordCrilteria.optimizeGroupName}%'
AND ck.fCreateTime >= #{customerKeywordCrilteria.creationFromTime}
AND ck.fCreateTime <= #{customerKeywordCrilteria.creationToTime}
AND ck.fType = #{customerKeywordCrilteria.entryType}
AND fCurrentPosition > 0 and fCurrentPosition <= #{customerKeywordCrilteria.position}     
AND ck.fCurrentPosition =  0    
AND fTerminalType =  #{customerKeywordCrilteria.terminalType} 

    ORDER BY ${customerKeywordCrilteria.orderElement}  DESC

update.if:
UPDATE t_customer_keyword SET fTitle = NULL, fCaptureTitleQueryTime = NULL, fCapturedTitle = 0 where 1 = 1


    and fCustomerUuid=#{customerKeyword.customerUuid}


    and fTerminalType = #{customerKeyword.terminalType}


    and fType = #{customerKeyword.type}


    and fStatus = #{customerKeyword.status}
