mapper.select:
SELECT g.fUuid AS "uuid", g.fGroupName AS "groupName", u.fUserName AS "userName", g.fRemainingAccount AS "remainingAccount", g.fMaxInvalidCount AS "maxInvalidCount" FROM t_group g LEFT JOIN t_userinfo u ON u.fLoginName = g.fCreateBy WHERE 1 = 1
AND g.fGroupName = #{groupSettingCriteria.optimizedGroupName}
AND g.fTerminalType = #{groupSettingCriteria.terminalType}
AND EXISTS (SELECT 1 FROM t_group_setting gs WHERE gs.fGroupUuid = g.fUuid
AND gs.fOperationType > ''
AND gs.fOperationType = #{groupSettingCriteria.operationType}
AND gs.fOperationType IS NULL
)
AND g.fRemainingAccount > 0
ORDER BY g.fCreateTime DESC

mapper.update:
UPDATE t_group
SET fMaxInvalidCount = #{maxInvalidCount}
WHERE fUuid = #{uuid}
