mapper.select:
SELECT g.fUuid AS 'groupUuid', g.fGroupName AS 'groupName' FROM t_group g LEFT JOIN t_operation_combine toc ON toc.fUuid = g.fOperationCombineUuid WHERE g.fOperationCombineUuid = #{operationCombineUuid}
AND g.fGroupName LIKE '${groupName}%'
ORDER BY g.fCreateTime

mapper.update:
UPDATE t_group
SET fUpdateTime = NOW(),


        fOperationCombineUuid = NULL


        fOperationCombineUuid = #{operationCombineUuid}


WHERE fUuid IN

    #{uuid}

UPDATE t_group g
SET g.fOperationCombineUuid = NULL
WHERE g.fOperationCombineUuid = #{operationCombineUuid}

UPDATE t_group g
SET g.fOperationCombineUuid = NULL
WHERE g.fOperationCombineUuid = #{operationCombineUuid}
AND g.fGroupName IN (

        #{groupName}

)

UPDATE t_group
SET fUpdateTime = NOW(), fOperationCombineUuid = #{operationCombineUuid}
WHERE fGroupName = #{groupName}
