mapper.select:
SELECT g.fUuid AS "uuid", g.fGroupName AS "groupName", u.fUserName AS "userName", g.fRemainingAccount AS "remainingAccount" FROM t_group g LEFT JOIN t_userinfo u ON u.fLoginName = g.fCreateBy WHERE 1 = 1
AND g.fGroupName = #{groupSettingCriteria.optimizedGroupName}
AND g.fTerminalType = #{groupSettingCriteria.terminalType}
AND EXISTS (SELECT 1 FROM t_group_setting gs WHERE gs.fGroupUuid = g.fUuid
AND gs.fOperationType = #{groupSettingCriteria.operationType}
)
AND NOT EXISTS (SELECT 1 FROM t_group_setting gs WHERE gs.fGroupUuid = g.fUuid)
ORDER BY g.fCreateTime DESC
