mapper.select:
SELECT

FROM t_industry_info tii
WHERE 1 = 1
AND tii.fUserID = #{industryCriteria.loginName}
AND tii.fSearchEngine = #{industryCriteria.searchEngine}
AND tii.fIndustryName = #{industryCriteria.industryName}
AND tii.fTerminalType = #{industryCriteria.terminalType}
AND tii.fStatus = #{industryCriteria.status}

mapper.update:
UPDATE t_industry_info
SET fUserID = #{userID}
WHERE fUuid IN

    #{uuid}

mapper.delete:
DELETE FROM t_industry_info
WHERE fUuid IN

    #{uuid}
