mapper.select:
SELECT
FROM t_machine_info mi WHERE mi.fTerminalType = #{machineInfoCriteria.terminalType} AND mi.fClientID like '%${machineInfoCriteria.clientID}%'
AND mi.fGroup = #{machineInfoCriteria.groupName}
AND mi.fGroup like '%${machineInfoCriteria.groupName}%'
AND mi.fUsingOperationType like '${machineInfoCriteria.operationType}%' AND mi.fUpgradeFailedReason like '%${machineInfoCriteria.upgradeFailedReason}%' AND mi.fVersion like concat(#{machineInfoCriteria.version},'%') AND mi.fTargetVersion like concat(#{machineInfoCriteria.targetVersion},'%') AND mi.fCity like concat(#{machineInfoCriteria.city},'%') AND mi.fValid = #{machineInfoCriteria.valid} AND (mi.fContinuousFailCount > 5 OR mi.fTenMinsLastVisitTime < NOW()) and mi.fValid = 1 AND mi.fRenewalDate < DATE_ADD(CURRENT_DATE(), INTERVAL -3 DAY) and mi.fValid = 1 AND (mi.fGroup is null or mi.fGroup = '') AND mi.fGroup > '' AND mi.fStartUpStatus = #{machineInfoCriteria.startUpStatus} AND mi.fRunningProgramType = #{machineInfoCriteria.runningProgramType} AND (mi.fHost is null or mi.fHost = '') AND (mi.fVersion <> mi.fTargetVersion) AND (mi.fPassword <> mi.fTargetVPSPassword) AND mi.fDownloadProgramType > '' AND (mi.fVPSBackendSystemComputerID = #{machineInfoCriteria.vpsBackendSystemComputerID}) AND mi.fSwitchGroupName = #{machineInfoCriteria.switchGroupName} AND mi.fSwitchGroupName IN
#{switchGroup}
ORDER BY ${machineInfoCriteria.orderBy}

