UPDATE t_machine_info
SET fTargetVersion = #{clientUpgrade.targetVersion}
WHERE fVersion = #{clientUpgrade.version}
AND fRunningProgramType = #{clientUpgrade.programType}
AND fTerminalType = #{clientUpgrade.terminalType}
AND (fTargetVersion is null or fTargetVersion <> #{clientUpgrade.targetVersion})
LIMIT #{clientUpgrade.maxUpgradeCount}

mapper.select:
SELECT cs.fGroup AS "group", cs.fTerminalType AS "terminalType", SUM(IF(DATE_ADD(cs.fLastVisitTime, INTERVAL IF(10 > cs.fPageNo * 3, 15, cs.fPageNo * 3 + 5) MINUTE) < NOW(), 1, 0)) AS 'unworkMachineCount', SUM(cs.fIdleTotalMinutes) AS "idleTotalMinutes", COUNT(1) AS 'totalMachineCount' FROM t_machine_info cs WHERE cs.fValid = 1 AND cs.fTerminalType = #{customerKeywordRefreshStatInfoCriteria.terminalType}
AND cs.fGroup like '${customerKeywordRefreshStatInfoCriteria.groupName}%' AND EXISTS (SELECT 1 FROM t_customer_keyword ck WHERE ck.fType = #{customerKeywordRefreshStatInfoCriteria.entryType} AND ck.fOptimizeGroupName like '${customerKeywordRefreshStatInfoCriteria.groupName}%')
GROUP BY cs.fGroup, cs.fTerminalType ORDER BY cs.fGroup

mapper.update:
UPDATE t_machine_info
SET
fLastVisitTime = NOW(),
fTenMinsLastVisitTime = DATE_ADD(NOW(), INTERVAL 10 MINUTE ),
fPageNo = #{pageNo}
WHERE fClientID = #{clientID}

UPDATE t_machine_info
SET fRestartStatus = #{restartStatus}, fRestartTime = now(), fThreeMinsRestartTime = DATE_ADD(NOW(), INTERVAL 3 MINUTE ), fTenMinsRestartTime = DATE_ADD(NOW(), INTERVAL 10 MINUTE ), fRestartOrderingTime = now()
WHERE fClientID = #{clientID}

mapper.select:
SELECT cs.fGroup as 'group', cs.fTerminalType as terminalType, COUNT(1) AS 'count' FROM t_client_status cs
and cs.fGroup LIKE '%${group}%' and cs.fTerminalType = #{terminalType}
GROUP BY cs.fGroup, cs.fTerminalType ORDER BY cs.fGroup

