mapper.select:
SELECT qzkri.fUuid AS "uuid", qzkri.fQZSettingUuid AS "qzSettingUuid", qzkri.fTerminalType AS "terminalType", qzkri.fWebSiteType AS "websiteType", qzkri.fTopTen AS "topTen", qzkri.fTopTwenty AS "topTwenty", qzkri.fTopThirty AS "topThirty", qzkri.fTopForty AS "topForty", qzkri.fTopFifty AS "topFifty", qzkri.fDate AS "date", qzkri.fFullDate AS "fullDate", qzkri.fIncrease AS "increase", qzkri.fIpRoute AS "ipRoute", qzkri.fBaiduWeight AS "baiduWeight", qzkri.fRecord AS "baiduRecord", qzkri.fAchieveLevel AS "achieveLevel", qzkri.fSumSeries AS "sumSeries", qzkri.fDifferenceValue AS "differenceValue", qzkri.fCurrentPrice AS "currentPrice" FROM t_qz_keyword_rank_info qzkri WHERE qzkri.fQZSettingUuid = #{qzSettingUuid}
AND qzkri.fBaiduWeight = #{baiduWeight}
AND qzkri.fIncrease >= 0.1
AND qzkri.fIncrease <= -0.1
AND qzkri.fTerminalType = #{terminalType}
