mapper.select:
SELECT qzkri.fUuid AS 'uuid', qzkri.fQZSettingUuid AS 'qzSettingUuid', qzkri.fTerminalType AS 'terminalType', qzkri.fWebSiteType AS 'websiteType', qzkri.fDataProcessingStatus AS 'dataProcessingStatus', qzkri.fTopTen AS 'topTen', qzkri.fTopTwenty AS 'topTwenty', qzkri.fTopThirty AS 'topThirty', qzkri.fTopForty AS 'topForty', qzkri.fTopFifty AS 'topFifty', qzkri.fTopHundred AS 'topHundred', qzkri.fCreateTopTenNum AS 'createTopTenNum', qzkri.fCreateTopFiftyNum AS 'createTopFiftyNum', qzkri.fDate AS 'date', qzkri.fFullDate AS 'fullDate', qzkri.fIncrease AS 'increase', qzkri.fIpRoute AS 'ipRoute', qzkri.fRecord AS 'baiduRecord', qzkri.fAchieveLevel AS 'achieveLevel', qzkri.fSumSeries AS 'sumSeries', qzkri.fDifferenceValue AS 'differenceValue', qzkri.fCurrentPrice AS 'currentPrice', qzkri.fBaiduRecordFullDate AS 'baiduRecordFullDate', qzkri.fAchieveTime AS 'achieveTime' FROM t_qz_keyword_rank_info qzkri WHERE qzkri.fQZSettingUuid = #{qzSettingUuid}
AND qzkri.fTerminalType = #{terminalType}
AND qzkri.fWebSiteType = #{websiteType}

SELECT tqkri.fUuid AS 'uuid', CONCAT("'", IF(MONTH(tqkri.fCreateTime) < 10, CONCAT('0', MONTH(tqkri.fCreateTime)), MONTH(tqkri.fCreateTime)), '-', IF(DAY(tqkri.fCreateTime) < 10, CONCAT('0', DAY(tqkri.fCreateTime)), DAY(tqkri.fCreateTime)), "'") AS 'createMonthDay' FROM t_qz_keyword_rank_info tqkri WHERE tqkri.fDataProcessingStatus = 1 AND tqkri.fQZSettingUuid = #{qzSettingUuid}
AND tqkri.fTerminalType = #{terminalType}
AND tqkri.fWebsiteType = #{websiteType}
LIMIT 1

SELECT
    COUNT((qk.fIncrease <= #{lowerValue} OR NULL) AND (qk.fOneWeekDifference <= #{downOneWeekDiff} OR NULL)) AS 'downNum',
    COUNT(qk.fIncrease = 0 OR NULL) AS 'unchangedNum',
    COUNT((qk.fIncrease >= #{upperValue} OR NULL) AND (qk.fOneWeekDifference >= #{upperOneWeekDiff} OR NULL)) AS 'upNum',
    COUNT(qk.fTodayDifference <0 OR NULL) AS 'downDifferenceNum',
    COUNT(qk.fTodayDifference = 0 OR NULL) AS 'unchangedDifferenceNum',
    COUNT(qk.fTodayDifference >0 OR NULL) AS 'upDifferenceNum',
    COUNT(qk.fAchieveLevel > 0 OR NULL) AS 'atLeastStandardNum',
    COUNT(qk.fAchieveLevel = 0 OR NULL) AS 'neverStandardNum',
    COUNT(qk.fDifferenceValue <= #{differenceValue} OR NULL) AS 'closeStandardNum'
FROM t_qz_keyword_rank_info qk
LEFT JOIN t_qz_setting qs
ON qs.fUuid = qk.fQZSettingUuid
LEFT JOIN t_qz_operation_type qot
ON qot.fQZSettingUuid = qk.fQZSettingUuid
WHERE qk.fTerminalType = #{criteria.terminalType}
AND qot.fOperationtype = #{criteria.terminalType}
AND qot.fIsDeleted = 0
AND qk.fDataProcessingStatus = 1
AND qs.fCustomerUuid = #{criteria.customerUuid}
AND qs.fDomain = #{criteria.domain}
AND qs.fSearchEngine = #{criteria.searchEngine}


        AND qot.fGroup = #{criteria.group}



AND qs.fUpdateStatus = #{criteria.updateStatus}
AND qs.fStatus = #{criteria.status}
AND qs.fRenewalStatus = #{criteria.renewalStatus}
AND qk.fWebsiteType = #{criteria.standardSpecies}
AND qot.fOptimizationType = #{criteria.optimizationType}
AND qs.fRenewalStatus = #{criteria.renewalStatus}

    AND EXISTS (SELECT 1 FROM t_qz_category_tag qc WHERE qc.fQZSettingUuid = qs.fUuid AND qc.fTagName = #{criteria.categoryTag})


    AND EXISTS (
        SELECT 1 FROM t_machine_info mi
        WHERE mi.fUsingOperationType = #{criteria.operationType}
        AND mi.fGroup = qot.fGroup
        AND mi.fTerminalType = #{criteria.terminalType}
    )

AND qs.fCreateTime >= #{criteria.createTime}
AND qs.fCreateTime <= #{criteria.createTimePrefix}

AND EXISTS (SELECT 1 FROM t_customer c WHERE c.fUuid = qs.fCustomerUuid AND c.fUserID = #{criteria.loginName})


    AND EXISTS (
        SELECT 1
        FROM t_customer c
        LEFT JOIN	t_userinfo ui
        ON c.fUserID = ui.fLoginName
        LEFT JOIN t_organization o
        ON ui.fOrganizationID = o.fUuid
        WHERE c.fUuid = qs.fCustomerUuid
        AND ui.fStatus = 0
        AND o.fUuid = #{criteria.organizationID}
    )

SELECT qzkri.fUuid AS 'uuid'
FROM t_qz_keyword_rank_info qzkri
WHERE qzkri.fQZSettingUuid = #{qzSettingUuid}
AND EXISTS(SELECT 1 FROM t_qz_setting qs WHERE qs.fUuid = qzkri.fQZSettingUuid)
AND qzkri.fTerminalType = #{terminalType}
AND qzkri.fWebsiteType = 'aiZhan'

SELECT qk.fUuid AS 'qkId', qk.fQZSettingUuid AS 'qsId', qk.fTerminalType AS 'terminalType', qk.fWebsiteType AS 'websiteType', qk.fDataProcessingStatus AS 'processingType', qk.fTopTen AS 'topTen', qk.fTopTwenty AS 'topTwenty', qk.fTopThirty AS 'topThirty', qk.fTopForty AS 'topForty', qk.fTopFifty AS 'topFifty', qk.fTopHundred AS 'topHundred', qk.fDate AS 'date', qk.fIncrease AS 'increase', qk.fTodayDifference AS 'todayDiffVal', qk.fOneWeekDifference AS 'oneWeekDiffVal', qk.fBaiduRecordFullDate AS 'recordDate', qk.fRecord AS 'record', qk.fCreateTopTenNum AS 'initTopTenNum', qk.fCreateTopFiftyNum AS 'initTopFiftyNum' FROM t_qz_keyword_rank_info qk WHERE qk.fQZSettingUuid = #{qzSettingUuid}
AND qk.fWebsiteType = 'xt'
AND qk.fWebsiteType = 'designationWord'
LIMIT #{limitRow}
