mapper.select:
SELECT qzkri.fUuid AS "uuid", qzkri.fQZSettingUuid AS "qzSettingUuid", qzkri.fTerminalType AS "terminalType", qzkri.fWebSiteType AS "websiteType", qzkri.fTopTen AS "topTen", qzkri.fTopTwenty AS "topTwenty", qzkri.fTopThirty AS "topThirty", qzkri.fTopForty AS "topForty", qzkri.fTopFifty AS "topFifty", qzkri.fDate AS "date", qzkri.fFullDate AS "fullDate", qzkri.fIncrease AS "increase", qzkri.fIpRoute AS "ipRoute", qzkri.fBaiduWeight AS "baiduWeight", qzkri.fRecord AS "baiduRecord", qzkri.fAchieveLevel AS "achieveLevel", qzkri.fSumSeries AS "sumSeries", qzkri.fDifferenceValue AS "differenceValue", qzkri.fCurrentPrice AS "currentPrice", qzkri.fBaiduRecordFullDate AS "baiduRecordFullDate" FROM t_qz_keyword_rank_info qzkri WHERE qzkri.fQZSettingUuid = #{qzSettingUuid}
AND qzkri.fBaiduWeight = #{qzSettingSearchCriteria.baiduWeight}
AND qzkri.fIncrease >= #{qzSettingSearchCriteria.upperValue}
AND qzkri.fIncrease <= #{qzSettingSearchCriteria.lowerValue}
AND qzkri.fTerminalType = #{qzSettingSearchCriteria.terminalType}

SELECT
    tqkri.fUuid AS 'uuid'
FROM t_qz_keyword_rank_info tqkri
WHERE 1 = 1
 AND tqkri.fQZSettingUuid = #{qzSettingUuid} 
 AND tqkri.fTerminalType = #{terminalType}
