mapper.select:
SELECT
    qs.fUuid AS 'uuid',
    c.fUserID AS 'userID',
    qs.fCustomerUuid AS 'customerUuid',
    c.fContactPerson AS 'contactPerson',
    o.fOrganizationName AS 'organizationName',
    qs.fDomain AS 'domain',
    qs.fSearchEngine  AS 'searchEngine',
    qs.fBearPawNumber AS 'bearPawNumber',


            qs.fPcGroup AS 'pcGroup',


            qs.fPhoneGroup AS 'phoneGroup',


    qs.fType AS 'type',
    qs.fIsMonitor AS 'fIsMonitor',
    qs.fIsReady AS 'fIsReady',
    qs.fCreateTime AS 'createTime',
    qs.fStatus AS 'status',
    qs.fRenewalStatus AS 'renewalStatus',
    qs.fAutoCrawlKeywordFlag AS 'autoCrawlKeywordFlag'
FROM t_qz_setting qs
JOIN t_customer c
ON qs.fCustomerUuid = c.fUuid
LEFT JOIN t_userinfo u
ON u.fLoginName = c.fUserID
LEFT JOIN t_organization o
ON o.fUuid = u.fOrganizationID
WHERE 1 = 1
AND c.fUserID = #{qzSettingSearchCriteria.loginName}
AND o.fUuid = #{qzSettingSearchCriteria.organizationID}
AND qs.fCustomerUuid = #{qzSettingSearchCriteria.customerUuid}
AND qs.fDomain = #{qzSettingSearchCriteria.domain}


        AND qs.fSearchEngine = #{qzSettingSearchCriteria.searchEngine}





        AND qs.fPcGroup > ''


        AND qs.fPcGroup = #{qzSettingSearchCriteria.group}


        AND qs.fPhoneGroup > ''


        AND qs.fPhoneGroup = #{qzSettingSearchCriteria.group}


AND qs.fUpdateStatus = #{qzSettingSearchCriteria.updateStatus}
AND qs.fStatus = #{qzSettingSearchCriteria.status}
AND qs.fRenewalStatus = #{qzSettingSearchCriteria.renewalStatus}
AND qs.fIsMonitor = #{qzSettingSearchCriteria.hasMonitor}
AND qs.fIsReady = #{qzSettingSearchCriteria.hasReady}

    AND EXISTS (
        SELECT 1
        FROM t_qz_charge_rule qcr
        LEFT JOIN t_qz_operation_type qot
        ON qcr.fQZOperationTypeUuid = qot.fUuid
        WHERE qs.fUuid = qot.fQZSettingUuid
        AND qot.fOperationtype = #{qzSettingSearchCriteria.terminalType}
        AND qcr.fStandardSpecies = #{qzSettingSearchCriteria.standardSpecies}
    )


    AND EXISTS (
        SELECT 1
        FROM t_qz_operation_type qot
        WHERE qs.fUuid = qot.fQZSettingUuid
        AND qot.fOperationtype = #{qzSettingSearchCriteria.terminalType}
        AND qot.fOptimizationType = #{qzSettingSearchCriteria.optimizationType}
        AND qot.fIsDeleted = 0
    )


    AND EXISTS (
        SELECT 1
        FROM t_qz_keyword_rank_info qk
        WHERE qk.fQZSettingUuid = qs.fUuid
        AND qk.fDataProcessingStatus = 1
        AND qk.fTerminalType = #{qzSettingSearchCriteria.terminalType}


            AND qk.fIncrease <= #{qzSettingSearchCriteria.lowerValue}
            AND qk.fOneWeekDifference <= #{qzSettingSearchCriteria.downOneWeekDiff}


            AND qk.fIncrease >= #{qzSettingSearchCriteria.upperValue}
            AND qk.fOneWeekDifference >= #{qzSettingSearchCriteria.upOneWeekDiff}


            AND qk.fAchieveLevel > 0
            AND EXISTS (
                SELECT 1
                FROM t_qz_operation_type qot
                WHERE qot.fQZSettingUuid = qk.fQZSettingUuid
                AND qot.fOperationtype = qk.fTerminalType
                AND qot.fOperationtype = #{qzSettingSearchCriteria.terminalType}
                AND qot.fIsDeleted = 0
                AND qot.fStandardTime > ''
            )


            AND qk.fAchieveLevel = 0
            AND EXISTS (
                SELECT 1
                FROM t_qz_operation_type qot
                WHERE qot.fQZSettingUuid = qk.fQZSettingUuid
                AND qot.fOperationtype = qk.fTerminalType
                AND qot.fOperationtype = #{qzSettingSearchCriteria.terminalType}
                AND qot.fIsDeleted = 0
            )


            AND qk.fDifferenceValue <= #{qzSettingSearchCriteria.differenceValue}


            AND qk.fIncrease = 0


            AND qk.fTodayDifference < 0


            AND (qk.fTodayDifference = 0 OR qk.fTodayDifference IS NULL)


            AND qk.fTodayDifference > 0


    )


    AND EXISTS (SELECT 1 FROM t_qz_category_tag qc WHERE qc.fQZSettingUuid = qs.fUuid AND qc.fTagName = #{qzSettingSearchCriteria.categoryTag})


    AND EXISTS (SELECT 1 FROM t_machine_info mi WHERE mi.fUsingOperationType = #{qzSettingSearchCriteria.operationType}


            AND mi.fGroup = qs.fPcGroup


            AND mi.fGroup = qs.fPhoneGroup


    AND mi.fTerminalType = #{qzSettingSearchCriteria.terminalType})

AND qs.fCreateTime >= #{qzSettingSearchCriteria.createTime}
AND qs.fCreateTime <= #{qzSettingSearchCriteria.createTimePrefix}


        AND EXISTS (select qs.fUuid from t_qz_operation_type ot where qs.fUuid = ot.fQZSettingUuid and ot.fNextChargeDate = CURDATE())


        AND EXISTS (select qs.fUuid from t_qz_operation_type ot where qs.fUuid = ot.fQZSettingUuid and ot.fNextChargeDate < CURDATE())


        AND EXISTS (select qs.fUuid from t_qz_operation_type ot where qs.fUuid = ot.fQZSettingUuid and ot.fNextChargeDate BETWEEN
        date_add(CURDATE(), INTERVAL 1 DAY) AND date_add(CURDATE(), INTERVAL 3 DAY))


        AND EXISTS (select qs.fUuid from t_qz_operation_type ot where qs.fUuid = ot.fQZSettingUuid and ot.fNextChargeDate BETWEEN
        date_add(CURDATE(), INTERVAL 4 DAY) AND date_add(CURDATE(), INTERVAL 7 DAY))





ORDER BY qs.fUpdateEndTime

mapper.update:
UPDATE t_qz_setting
SET fPcGroup = #{qzSetting.pcGroup},fPhoneGroup = #{qzSetting.phoneGroup}
WHERE fUuid = #{qzSetting.uuid}

UPDATE t_qz_setting
SET fStatus = #{status}

    , fAutoCrawlKeywordFlag = FALSE

WHERE fUuid IN

    #{item}

UPDATE t_qz_setting
SET fPcKeywordExceedMaxCount = #{pcKeywordExceedMaxCount}
WHERE fUuid IN

    #{item}

UPDATE t_qz_setting
SET fPhoneKeywordExceedMaxCount = #{phoneKeywordExceedMaxCount}
WHERE fUuid IN

    #{item}

update t_qz_setting

    fCrawlerTime = #{qzSetting.crawlerTime},
    fCrawlerStatus = #{qzSetting.crawlerStatus}

WHERE fUuid = #{qzSetting.uuid}
