mapper.select:
SELECT u.fUuid AS `uuid`, u.fLoginName AS `loginName`, u.fUserName AS `userName`, u.fPassword AS `password`, u.fSex AS `sex`, u.fStatus AS `status`, u.fOrganizationID AS `organizationID`, u.fPhone AS `phone`, u.fcreateTime AS `createTime`, og.fOrganizationName AS `organizationName`, group_concat(r.`fRoleName`) AS `rolesList` FROM t_userinfo u LEFT JOIN t_user_role ur ON u.fUuid = ur.fUserID LEFT JOIN t_role r ON ur.fRoleID = r.fUuid LEFT JOIN t_organization og ON og.fUuid= u.fOrganizationID
u.fUserName = #{userName}
u.fOrganizationID = #{organizationID}
GROUP BY u.fUuid
