mapper.select:
        SELECT

        FROM t_website w

            JOIN t_friendly_link fl ON fl.fWebsiteUuid = w.fUuid


            JOIN t_advertising a ON a.fWebsiteUuid = w.fUuid

		WHERE 1 = 1
		AND w.fWebsiteName LIKE '%${websiteCriteria.websiteName}%'
		AND w.fDomain LIKE '%${websiteCriteria.domain}%'
		AND w.fIndustry = #{websiteCriteria.industryType}
		AND w.fWebsiteType = #{websiteCriteria.websiteType}
		AND fl.fFriendlyLinkUrl = #{websiteCriteria.friendlyLinkUrl}
		AND w.fUpdateSalesInfoSign = #{websiteCriteria.updateSalesInfoSign}
		AND w.fSynchronousFriendlyLinkSign = #{websiteCriteria.synchronousFriendlyLinkSign}
		AND w.fSynchronousAdvertisingSign = #{websiteCriteria.synchronousAdvertisingSign}
		AND w.fBackgroundLoginStatus = #{websiteCriteria.backgroundLoginStatus}
		AND w.fSftpStatus = #{websiteCriteria.sftpStatus}
		AND w.fIndexFileStatus = #{websiteCriteria.indexFileStatus}



					AND w.fDatabaseStatus = '0'


					AND w.fDatabaseStatus != '0'



		AND a.fAdvertisingTagname = #{websiteCriteria.advertisingTagname}
		AND w.fAccessFailCount = #{websiteCriteria.accessFailCount}
		GROUP BY w.fUuid
		ORDER BY w.fLastAccessTime

update.foreach:
UPDATE t_website w
w.fBackgroundLoginStatus = #{websiteCheckResultVO.backgroundLoginStatus}, w.fSftpStatus = #{websiteCheckResultVO.sftpStatus}, w.fIndexFileStatus = #{websiteCheckResultVO.indexFileStatus}, w.fDatabaseStatus = #{websiteCheckResultVO.databaseStatus}, w.fUpdateTime = NOW()
w.fUuid = #{websiteCheckResultVO.uuid}
